// HYBRID INTELLIGENCE ROUTER
// Automatically routes to AI Agent (API) or Stealth Browser based on platform capabilities

const payload = $json;
const { sourcePlatform, destinationPlatform, action } = payload;

// Platform capabilities database
const platformCapabilities = {
  'shopify': { hasAPI: true, apiSpeed: 'lightning', reliability: 99.8 },
  'woocommerce': { hasAPI: true, apiSpeed: 'fast', reliability: 99.5 },
  'etsy': { hasAPI: true, apiSpeed: 'fast', reliability: 99.2 },
  'gumroad': { hasAPI: true, apiSpeed: 'fast', reliability: 99.0 },
  'bigcommerce': { hasAPI: true, apiSpeed: 'fast', reliability: 99.3 },
  'payhip': { hasAPI: false, browserMethod: 'stealth', reliability: 97.8 },
  'teachable': { hasAPI: false, browserMethod: 'stealth', reliability: 97.5 },
  'thinkific': { hasAPI: false, browserMethod: 'stealth', reliability: 97.2 },
  'kajabi': { hasAPI: false, browserMethod: 'stealth', reliability: 96.8 },
  'podia': { hasAPI: false, browserMethod: 'stealth', reliability: 97.0 }
};

const sourceConfig = platformCapabilities[sourcePlatform.toLowerCase()];
const destConfig = platformCapabilities[destinationPlatform.toLowerCase()];

// Intelligent routing decision
let routingDecision;

if (sourceConfig?.hasAPI && destConfig?.hasAPI) {
  routingDecision = 'DUAL_API_LIGHTNING'; // Fastest possible
} else if (sourceConfig?.hasAPI && !destConfig?.hasAPI) {
  routingDecision = 'API_TO_BROWSER'; // Extract via API, migrate via browser
} else if (!sourceConfig?.hasAPI && destConfig?.hasAPI) {
  routingDecision = 'BROWSER_TO_API'; // Extract via browser, migrate via API
} else {
  routingDecision = 'DUAL_STEALTH_BROWSER'; // Full stealth mode
}

// Performance estimation
let estimatedTime, expectedReliability;

switch(routingDecision) {
  case 'DUAL_API_LIGHTNING':
    estimatedTime = '2-5 seconds per product';
    expectedReliability = '99.8%';
    break;
  case 'API_TO_BROWSER':
  case 'BROWSER_TO_API':
    estimatedTime = '15-30 seconds per product';
    expectedReliability = '98.5%';
    break;
  case 'DUAL_STEALTH_BROWSER':
    estimatedTime = '30-60 seconds per product';
    expectedReliability = '97.5%';
    break;
}

return [{
  json: {
    ...payload,
    hybridIntelligence: {
      routingDecision,
      sourceMethod: sourceConfig?.hasAPI ? 'AI_Agent' : 'Stealth_Browser',
      destMethod: destConfig?.hasAPI ? 'AI_Agent' : 'Stealth_Browser',
      estimatedTime,
      expectedReliability,
      sourceConfig,
      destConfig
    },
    timestamp: new Date().toISOString(),
    sessionId: $uuid()
  }
}];

// DUAL API LIGHTNING MODE - MAXIMUM SPEED
