console.log(`ðŸ”€ Hybrid Mode: ${hybridIntelligence.sourceMethod} â†’ ${hybridIntelligence.destMethod}`);

const results = [];
const startTime = Date.now();

for (const product of products) {
  try {
    let extractedProduct;

    // STEP 1: Intelligent Extraction
    if (hybridIntelligence.sourceMethod === 'AI_Agent') {
      console.log('âš¡ AI Agent extraction...');
      extractedProduct = await extractViaAPI(sourcePlatform, sourceCredentials, product);
    } else {
      console.log('ðŸ¥· Stealth Browser extraction...');
      extractedProduct = await extractViaBrowser(sourcePlatform, sourceCredentials, product);
    }

    // STEP 2: Intelligent Migration
