    } else {
      console.log('ðŸ¥· Stealth Browser migration...');
      migrationResult = await migrateViaBrowser(destinationPlatform, destCredentials, extractedProduct);
    }

    results.push({
      productId: product.id,
      success: true,
      extractionMethod: hybridIntelligence.sourceMethod,
      migrationMethod: hybridIntelligence.destMethod,
      hybridRoute: `${hybridIntelligence.sourceMethod}_TO_${hybridIntelligence.destMethod}`,
      result: migrationResult
    });

  } catch (error) {
    results.push({
      productId: product.id,
      success: false,
      error: error.message,
      hybridRoute: `${hybridIntelligence.sourceMethod}_TO_${hybridIntelligence.destMethod}`
    });
