function generateOptimizationRecommendations(results) {
  const recommendations = [];

  if (results.performance.successRate && parseFloat(results.performance.successRate) < 95) {
    recommendations.push('Consider upgrading to premium API access for higher reliability');
  }

  if (results.routingDecision === 'DUAL_STEALTH_BROWSER') {
    recommendations.push('Check if destination platform offers API access for faster migrations');
  }

  if (results.results.some(r => r.error?.includes('CAPTCHA'))) {
    recommendations.push('Implement advanced CAPTCHA bypass techniques for better automation');
  }

  return recommendations;
}

function generateNextSteps(results) {
  const steps = [];

  if (results.success) {
    steps.push('Verify migrated products on destination platform');
    steps.push('Set up monitoring for ongoing product sync');
  } else {
    steps.push('Review failed migrations and retry with enhanced settings');
    steps.push('Check platform credentials and permissions');
  }

  return steps;
}
